package types

import (
	"encoding/hex"
	"strings"
	"testing"

	"github.com/stretchr/testify/require"
)

func TestInitialCycleList(t *testing.T) {
	require := require.New(t)

	strs := []string{
		"",

		"",

		"",
	}

	cycleList := InitialCycleList()
	require.Equal(3, len(cycleList))
	require.Contains(strs, strings.ToUpper(hex.EncodeToString(cycleList[0])))
	require.Contains(strs, strings.ToUpper(hex.EncodeToString(cycleList[1])))
	require.Contains(strs, strings.ToUpper(hex.EncodeToString(cycleList[2])))
}
