package types

import (
	"encoding/hex"
	"fmt"

	"cosmossdk.io/math"
	paramtypes "github.com/cosmos/cosmos-sdk/x/params/types"
	"gopkg.in/yaml.v2"
)

var _ paramtypes.ParamSet = (*Params)(nil)

var (
	KeyCycleList      = []byte("CycleList")
	KeyMinStakeAmount = []byte("MinStakeAmount")
	// TODO: Determine the default value
	DefaultMinStakeAmount = math.NewInt(1_000_000) // one TRB
	InitialCycleList      = []string{
		//ETH/USD
		"",
		//BTC/USD
		"",
		//TRB/USD
		"",
	}
)

// ParamKeyTable the param key table for launch module
func ParamKeyTable() paramtypes.KeyTable {
	return paramtypes.NewKeyTable().RegisterParamSet(&Params{})
}

// NewParams creates a new Params instance
func NewParams(cycleList []string, minStakeAmount math.Int) Params {
	return Params{
		CycleList:      cycleList,
		MinStakeAmount: minStakeAmount,
	}
}

// DefaultParams returns a default set of parameters
func DefaultParams() Params {
	return NewParams(InitialCycleList, DefaultMinStakeAmount)
}

// ParamSetPairs get the params.ParamSet
func (p *Params) ParamSetPairs() paramtypes.ParamSetPairs {
	return paramtypes.ParamSetPairs{
		paramtypes.NewParamSetPair(KeyCycleList, &p.CycleList, validateCycleList),
		paramtypes.NewParamSetPair(KeyMinStakeAmount, &p.MinStakeAmount, validateMinStakeAmount),
	}
}

// Validate validates the set of params
func (p Params) Validate() error {
	return nil
}

// String implements the Stringer interface.
func (p Params) String() string {
	out, _ := yaml.Marshal(p)
	return string(out)
}

// validate cycleList queries param
func validateCycleList(v interface{}) error {
	cycleList, ok := v.([]string)
	if !ok {
		return fmt.Errorf("invalid parameter type: %T", v)
	}

	for _, query := range cycleList {
		// try encoding the query to ensure it's valid
		_, err := hex.DecodeString(query)
		if err != nil {
			return fmt.Errorf("invalid query in cyclelist: %s", err)
		}
	}
	return nil
}

// validateMinStakeAmount validates the MinStakeAmount param
func validateMinStakeAmount(v interface{}) error {
	_, ok := v.(math.Int)
	if !ok {
		return fmt.Errorf("invalid parameter type: %T", v)
	}

	return nil
}
